import React from 'react';
import { render, screen } from '@testing-library/react';
import '@testing-library/jest-dom/extend-expect';
import Block from '../TextCard/CardWithImage/Block';
import configureStore from 'redux-mock-store';
import { Provider } from 'react-intl-redux';
import { MemoryRouter } from 'react-router-dom';
import thunk from 'redux-thunk';

const middlewares = [thunk];
const mockStore = configureStore(middlewares);

const mock_fields = {
  '@type': 'testo_riquadro_immagine',
  image: {
    'content-type': 'image/jpeg',
    data: '',
    encoding: 'base64',
    filename: 'fisiatria.jpeg',
  },
  image_card_content: {
    blocks: [
      {
        data: {},
        depth: 0,
        entityRanges: [],
        inlineStyleRanges: [],
        key: '40dgi',
        text: 'Is this just fantasy?',
        type: 'unstyled',
      },
    ],
    entityMap: {},
  },
  image_card_title: {
    blocks: [
      {
        data: {},
        depth: 0,
        entityRanges: [],
        inlineStyleRanges: [],
        key: '61l6b',
        text: 'Is this the real life?',
        type: 'unstyled',
      },
    ],
    entityMap: {},
  },
  rightImage: true,
  showImage: true,
  sizeNatural: true,
  sizeImage: 's',
};

const mock_fields_m_image = {
  ...mock_fields,
  sizeImage: 'm',
};

const mock_fields_l_image = {
  ...mock_fields,
  sizeImage: 'l',
};
const mock_fields_no_image = {
  ...mock_fields,
  sizeImage: undefined,
  showImage: false,
  image: undefined,
};

const store = mockStore({
  intl: {
    locale: 'en',
    messages: {},
  },
});

test('View renders all fields', async () => {
  render(
    <Provider store={store}>
      <MemoryRouter>
        <Block data={mock_fields} block={{ id: '1234' }} />
      </MemoryRouter>
    </Provider>,
  );
  //titolo
  expect(
    screen.getByRole('heading', { name: /Is this the real life?/i }),
  ).toBeInTheDocument();

  //descrizione
  expect(screen.getByText(/Is this just fantasy?/i)).toBeInTheDocument();

  //immagine
  const image = document.querySelector('.img-wrapper');
  expect(image).toBeInTheDocument();

  //non alterare dimensioni naturali immagine
  const imageWrapper = document.querySelector('.img-responsive-wrapper div');
  expect(imageWrapper).not.toHaveClass('img-responsive');

  //allinea immagine a destra
  const cardBodyRow = document.querySelector('.card-body-row');
  expect(cardBodyRow).toHaveClass('revert-row');

  // rapporto dimensione immagine-testo
  const textCol = await screen.findByTestId('col-1');
  expect(textCol.getAttribute('class')).toContain('col-8');
});

test('View renders image as m size', async () => {
  render(
    <Provider store={store}>
      <MemoryRouter>
        <Block data={mock_fields_m_image} block={{ id: '1234' }} />
      </MemoryRouter>
    </Provider>,
  );
  // rapporto dimensione immagine-testo
  const textCol = await screen.findByTestId('col-1');
  expect(textCol.getAttribute('class')).toContain('col-6');
});

test('View renders image as l size', async () => {
  render(
    <Provider store={store}>
      <MemoryRouter>
        <Block data={mock_fields_l_image} block={{ id: '1234' }} />
      </MemoryRouter>
    </Provider>,
  );
  // rapporto dimensione immagine-testo
  const textCol = await screen.findByTestId('col-1');
  expect(textCol.getAttribute('class')).toContain('col-4');
});

test('View renders block without image', async () => {
  render(
    <Provider store={store}>
      <MemoryRouter>
        <Block data={mock_fields_no_image} block={{ id: '1234' }} />
      </MemoryRouter>
    </Provider>,
  );
  // rapporto dimensione immagine-testo
  const textCol = await screen.findByTestId('col-1');
  expect(textCol.getAttribute('class')).toContain('col-12');
});
